import { createSlice } from "@reduxjs/toolkit";
const initialState = {
    weatherHistory: []
}

export const weatherSlice = createSlice({
    name: "weather",
    initialState,
    reducers: {
        insertCity: (state, action) => {
            const existingJson = state.weatherHistory.find((weather) => {
                console.log("weather.Location:", weather.Location);
                console.log("action.payload.Location:", action.payload.Location);
                return weather.Location === action.payload.Location;
            });
            console.log("State Data ", state.weatherHistory)
            if (!existingJson) {
                console.log("Action data ", action.payload)
                state.weatherHistory.push(action.payload);
            }
        }
    }
})


// Action creators are generated for each case reducer function
export const { insertCity } = weatherSlice.actions

export default weatherSlice.reducer
