import './App.css';
import Home from './components/Home';
import Navbar from './components/Navbar';
import Forcast from './components/Forcast';
import SearchPage from './components/SearchPage';
import axios from 'axios'
import { WeatherObjectCon } from './WeatherObjectCon'
import Loader from './Loader'
import { insertCity } from '../store/weatherSlice'

const WEATHER_API_KEY = 'f31dc2b8b49a27efda7c2d44d41aa637'

import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import { useDispatch } from 'react-redux';

function App() {

  const defaultLocation = ['Lahore Pakistan', 'Karachi Pakistan', 'Murree Pakistan', 'Berlin Germany']

  const dispatch = useDispatch();

  const handleSearch = async (e) => {
    e.preventDefault()
    try {
      setShowLoader(false)
      const response = await axios.get('https://api.openweathermap.org/data/2.5/weather', {
        params: {
          q: location,
          APPID: WEATHER_API_KEY,
        },
      });

      if (response.data) {
        const weatherObject = WeatherObjectCon(response.data.name, response.data.main.temp, response.data.main.pressure, response.data.main.humidity, response.data.wind.speed, response.data.clouds.all)
        setWeatherDetails(weatherObject)
        dispatch(insertCity(weatherObject))
      }
    } catch (error) {
      console.error(error);
      // Handle errors
    }
  }

  useEffect(() => {
    defaultLocation.map((city) => {
      setlocation(city)
      handleSearch()
    })
    setShowLoader(true)
  }, [])

  return (
    <div className="App">
      <Router>
        <Navbar />
        <Routes>
          <Route path='/' element={<Home />} />
          <Route path='/search' element={<SearchPage />} />
          <Route path='/forcast' element={<Forcast />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
