import React, { useState } from 'react'
import cloudy from "./../assets/Cloudy.jpg"
import axios from 'axios'
import { WeatherObjectCon } from './WeatherObjectCon'

const WEATHER_API_KEY = 'f31dc2b8b49a27efda7c2d44d41aa637'

const Home = () => {
    const [location, setlocation] = useState('')
    const [weatherDetails, setWeatherDetails] = useState({})

    const handleSearch = async (e) => {
        e.preventDefault()   
        try {
            const response = await axios.get('https://api.openweathermap.org/data/2.5/weather', {
              params: {
                q: location,
                APPID: WEATHER_API_KEY,
              },
            });

            if(response.data){
                setWeatherDetails( WeatherObjectCon(response.data.name, response.data.main.temp, response.data.main.pressure, response.data.main.humidity, response.data.wind.speed, response.data.clouds.all ))
            }
          } catch (error) {
            console.error(error);
            // Handle errors
          }
    }
    return (
        <div >
            <div className=" rounded w-500 shadow-xl m-10 p-5 border">
                <div className='flex justify-between'>
                    <button className='bg-blue p-3 rounded-md'>Get Current Location</button>
                    <form className="flex items-center ">
                        <label className="sr-only">Search</label>
                        <div className="relative w-full">
                            <input type="text" id="voice-search" className=" border  text-lg rounded-lg focus:ring-blue-500
                             focus:border-blue-500 block w-full pl-10 p-2.5"
                                placeholder="Search a city." required value={location} onChange={(e) => { setlocation(e.target.value) }} />
                        </div>
                        <button type="submit" className="inline-flex items-center py-2 px-3 ml-2 text-sm font-medium text-black
                         rounded-lg border hover:bg-blue focus:ring-4 focus:outline-none focus:ring-blue-300" onClick={(e) => handleSearch(e)}>
                            Search
                        </button>
                    </form>

                </div>


                <div className='text-center p-5 border m-5 mt-10 '>
                    <h1> <span className='text-lg'>Location:</span> {weatherDetails?.Location}</ h1>
                    <div className='flex justify-between h-250 items-center '>
                        <div className='flex flex-col justify-center'>
                            <img src={cloudy} alt='Cloudy not found' className='w-40 h-40 rounded-full' />
                            <h3>Cloudy: {weatherDetails?.Cloudy}</h3>
                        </div>
                        <h3>Temperature: {weatherDetails?.Temperature} C</h3>
                        <div>
                            <p>Wind: {weatherDetails?.Wind}</p>
                            <p>Humidity: {weatherDetails?.Humaidity} </p>
                            <p>Pressure: {weatherDetails?.Pressure}</p>
                        </div>
                    </div>


                </div>

            </div>
        </div>
    )
}

export default Home
